import React, { Suspense } from "react";
import { Routes, Route } from "react-router-dom";
import { Toaster } from "react-hot-toast";
import Header from "./components/Header"; // Only one import for Header
import Register from "./pages/Register";

// Lazy-loaded components
const Blogs = React.lazy(() => import("./pages/Blogs"));
const Login = React.lazy(() => import("./pages/Login"));
const Dashboard = React.lazy(() => import("./pages/Dashboard"));
const CreateBlog = React.lazy(() => import("./pages/CreateBlog"));

function App() {
  return (
    <>
      <Toaster />
      
      {/* The Header is rendered on all pages */}

      <Suspense fallback={<div>Loading...</div>}>
        <Routes>
          <Route path="/" element={<Header />} /> {/* Home page showing Blogs */}
          <Route path="/blogs" element={<Blogs />} /> {/* List of blogs */}
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/my-blogs" element={<Blogs />} /> {/* MyBlogs page */}
          <Route path="/create-blog" element={<CreateBlog />} />
        </Routes>
      </Suspense>
    </>
  );
}

export default App;
