import React from "react";

const BlogCard = ({ blog, onDelete, setSelectedBlog }) => {
  
  // Function to handle the edit action
  const handleEdit = (e) => {
    e.stopPropagation(); // Prevent bubbling to parent handlers
    setSelectedBlog(blog); // Set the selected blog to be edited
  };

  // Function to handle the delete action
  const handleDelete = (e) => {
    e.stopPropagation(); // Prevent bubbling to parent handlers
    if (window.confirm('Are you sure you want to delete this blog?')) {
      onDelete(blog); // Delete the blog if confirmed
    }
  };

  return (
    <div className="card">
      <img
        src={`http://localhost:8080/uploads/${blog.image}`}
        className="card-img-top"
        alt={blog.title}
        onClick={() => setSelectedBlog(blog)} // Optional: clicking image selects blog for edit
      />
      <div className="card-body">
        <h5 className="card-title">{blog.title}</h5>
        <p className="card-text">{blog.description}</p>
        <div>
          <button
            className="btn btn-primary me-2"
            onClick={handleEdit} // Trigger only edit on Edit button click
          >
            Edit
          </button>
          <button
            className="btn btn-danger"
            onClick={handleDelete} // Trigger only delete on Delete button click
          >
            Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default BlogCard;
