import React from "react";

const BlogCard = ({ blog, onDelete, setSelectedBlog }) => {
  
  const handleEdit = () => {
    // Set selected blog for update, calling the function passed from parent
    setSelectedBlog(blog);
  };

  const handleDelete = () => {
    // Call the onDelete function passed down from the parent
    onDelete(blog);
  };

  return (
    <div className="card" style={{ width: "18rem" }}>
      <img
        src={`http://localhost:8080/uploads/${blog.image}`} // Full image path
        className="card-img-top"
        alt={blog.title}
        style={{ height: "200px", objectFit: "cover" }} // Adjust image size to avoid distortion
      />
      <div className="card-body">
        <h5 className="card-title">{blog.title}</h5>
        <p className="card-text">
          {blog.description.length > 100
            ? blog.description.slice(0, 100) + "..." // Truncate the description if it's too long
            : blog.description}
        </p>
        <div>
          <button
            className="btn btn-primary me-2"
            onClick={handleEdit} // Trigger edit on clicking Edit
          >
            Edit
          </button>
          <button
            className="btn btn-danger"
            onClick={handleDelete} // Trigger delete on clicking Delete
          >
            Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default BlogCard;
