// frontend/src/components/BlogCard.js
import React from "react";

const BlogCard = ({ blog, onDelete, setSelectedBlog }) => {
  const handleEdit = () => {
    // Set selected blog for update
    setSelectedBlog(blog);
  };

  const handleDelete = () => {
    // Call the onDelete function passed down from the parent
    onDelete(blog);
  };

  return (
    <div className="card">
      <img
        src={`http://localhost:8080/uploads/${blog.image}`}
        className="card-img-top"
        alt={blog.title}
      />
      <div className="card-body">
        <h5 className="card-title">{blog.title}</h5>
        <p className="card-text">{blog.description}</p>
        <div>
          <button
            className="btn btn-primary me-2"
            onClick={handleEdit} // Only trigger edit on clicking Edit
          >
            Edit
          </button>
          <button
            className="btn btn-danger"
            onClick={handleDelete} // Only trigger delete on clicking Delete
          >
            Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default BlogCard;
