import React, { useState } from "react";

const BlogCard = ({ blog, onDelete, setSelectedBlog }) => {
  const [showFullDescription, setShowFullDescription] = useState(false); // State to toggle full description

  const handleEdit = () => {
    // Set selected blog for update
    setSelectedBlog(blog);
  };

  const handleDelete = () => {
    // Call the onDelete function passed down from the parent
    onDelete(blog);
  };

  const handleToggleDescription = () => {
    // Toggle the description visibility
    setShowFullDescription(!showFullDescription);
  };

  // Limit the description to 5 words
  const maxWords = 5;
  const truncatedDescription =
    blog.description.split(" ").slice(0, maxWords).join(" ") + (blog.description.split(" ").length > maxWords ? "..." : "");

  return (
    <div className="card">
      <img
        src={`http://localhost:8080/uploads/${blog.image}`}
        className="card-img-top"
        alt={blog.title}
      />
      <div className="card-body">
        <h5 className="card-title">{blog.title}</h5>
        
        {/* Show truncated or full description based on state */}
        <p className="card-text">
          {showFullDescription ? blog.description : truncatedDescription}
        </p>

        {/* Read More button if description is long */}
        {blog.description.split(" ").length > maxWords && (
          <button
            className="btn btn-link p-0"
            onClick={handleToggleDescription}
          >
            {showFullDescription ? "Read Less" : "Read More"}
          </button>
        )}
        <br></br>

        <div>
          <button
            className="btn btn-primary me-2"
            onClick={handleEdit} // Only trigger edit on clicking Edit
          >
            Edit
          </button>
          <button
            className="btn btn-danger"
            onClick={handleDelete} // Only trigger delete on clicking Delete
          >
            Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default BlogCard;
